sampleQuery = "SELECT COUNT (*),PROGX.ORG_ID, ORG_NM FROM PUBLIC.PATIENT_ORGANISATION_XREF PROGX JOIN PUBLIC.ORGANISATION_REF ORG ON ORG.ORG_ID=PROGX.ORG_ID WHERE PROGX.ORG_GRP_ID=%(ORG_GRP_ID)s AND PROGX.ORG_ID=%(ORG_ID)s GROUP BY PROGX.ORG_ID,ORG_NM ORDER BY ORG_NM"
fetchUserType= "SELECT EXISTS(SELECT USER_TYPE FROM PUBLIC.EMPLOYEES_INFO_REF WHERE USER_TYPE='superadmin' AND EMP_ID=%(empId)s)USER_TYPE"
queryToFetchPatientCountOfGroup= "SELECT COUNT (*),PROGX.ORG_ID, ORG_NM FROM PUBLIC.PATIENT_ORGANISATION_XREF PROGX JOIN PUBLIC.ORGANISATION_REF ORG ON ORG.ORG_ID=PROGX.ORG_ID WHERE PROGX.ORG_GRP_ID=:ORG_GRP_ID"
